// 简化版本 - 只保留Start按钮功能
document.addEventListener('DOMContentLoaded', function() {
    let isTransitioning = false;
    
    // 获取DOM元素
    const pixelAboutSection = document.querySelector('.pixel-about-container');
    const mastheadWrapper = document.querySelector('.masthead-wrapper');
    const pageAboutBackground = document.querySelector('.page__about-background');
    const startButton = document.querySelector('.pixel-start-button');
    
    // 确保像素风界面初始可见
    if (pixelAboutSection) {
        pixelAboutSection.style.display = 'block';
    }
    if (mastheadWrapper) {
        mastheadWrapper.style.display = 'none';
    }
    if (pageAboutBackground) {
        pageAboutBackground.style.display = 'none';
    }
    
    // Start按钮点击事件 - 唯一的切换方式
    if (startButton) {
        startButton.addEventListener('click', function() {
            console.log('Start button clicked');
            isTransitioning = true;
            
            // 直接切换到正常内容，无渐变
            if (pixelAboutSection) {
                pixelAboutSection.style.display = 'none';
            }
            if (mastheadWrapper) {
                mastheadWrapper.style.display = 'block';
                mastheadWrapper.style.opacity = '1';
            }
            if (pageAboutBackground) {
                pageAboutBackground.style.display = 'block';
            }
            window.scrollTo(0, 0);
            isTransitioning = false;
        });
    }
    
    // 取消所有滚动功能 - 不再有滚动触发的切换
});
